
public class UnaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int num =12;
      int num1 =25;
      boolean c=true;
      boolean d=false;
      System.out.println(~num);
      System.out.println(num++);  
      System.out.println(++num);  
      System.out.println(num--);  
      System.out.println(--num);
      System.out.println(!c);  
      System.out.println(!d);

	}

}
